import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import PaginaPrincipal from './PaginaPrincipal';
import Chatbot from './Chatbot';
import Donaciones from './Donaciones.jsx';
import IniciarSecion from './IniciarSesion';
import RegistroCoder from './RegistroCoder';
import usuario from './user.png';
import RegistroCrafter from './RegistroCrafter';
import SubirReto from './SubirReto';
import inicio from './logofoto.png'; // Asegúrate de tener la imagen en la ruta correcta
import './App.css'; // Asegúrate de definir el CSS correctamente

function App() {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <Router>
      <div className="app">
        <header className="app-header">
          <nav className="navbar">
            <ul className="nav-links">
              <li className="nav-item">
                <Link to="/">
                  <img src={inicio} alt="Inicio" className="logo" />
                </Link>
              </li>
              <li className="nav-item"><Link to="/donaciones">Donaciones</Link></li>
              <li className="nav-item2"><Link to="/sobre-nosotros">Sobre nosotros</Link></li>
              <li className="nav-item3"><Link to="/comentarios">Comentarios</Link></li>
              <li className="nav-item4"><Link to="/chatbot">Chat bot</Link></li>
              <li className="nav-item4"><Link to="/subirreto">Subir reto</Link></li>
            </ul>

            <button className="create-challenge-btn">+ Crear reto</button>

            <div className="user-profile">
              <div className="user-info" onClick={toggleDropdown}>
                <img src={usuario} alt="User Avatar" className="user-avatar" />
                <span>Keylan C</span>
                <span className="dropdown-arrow">▼</span>
              </div>
              {dropdownOpen && (
                <div className="dropdown-menu">
                  <ul>
                    <li><Link to="/cuenta">Cuenta</Link></li>
                    <li><Link to="/ajustes">Ajustes</Link></li>
                    <li><Link to="/logout">Cerrar sesión</Link></li>
                  </ul>
                </div>
              )}
            </div>
          </nav>
        </header>


        <footer className="app-footer">
          <p>© TimeData2024</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
