import React, { useState } from 'react';
import './SubirReto.css';

const Subirreto = () => {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      // Aquí puedes agregar la lógica para manejar la carga del archivo
    }
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const droppedFile = event.dataTransfer.files[0];
    if (droppedFile) {
      setFile(droppedFile);
      // Aquí puedes agregar la lógica para manejar la carga del archivo
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div className="subirreto">
      <div className="b16170a5347c77eee4653-welcome">
        <div className="b16170a5347c77eee4653-welcome1" />
      </div>
      <div className="sube-aqui-el">Sube aquí el archivo del reto</div>
      <div className="file-upload-area" onDrop={handleDrop} onDragOver={handleDragOver}>
        <div className="cargar-archivos">
          <input
            type="file"
            onChange={handleFileChange}
            style={{ display: 'none' }} // Oculta el input file
            id="file-upload"
          />
          <label htmlFor="file-upload">Cargar archivo</label>
        </div>
        <div className="o-arrastra-aqui">O arrastra aquí un archivo</div>
      </div>
      <img className="subirreto-child" alt="" src="Vector 3.svg" />
      <img className="subirreto-item" alt="" src="Vector 4.svg" />
    </div>
  );
};

export default Subirreto;
