import React, { useEffect, useState } from 'react';
import './cuenta.css'; // Asegúrate de que la ruta sea correcta

function Cuenta({ username, email, fullName, registrationDate, updateNavbarProfilePicture }) {
    const [profilePicture, setProfilePicture] = useState('');

    useEffect(() => {
        const storedProfilePicture = localStorage.getItem('profilePicture');
        if (storedProfilePicture) {
            setProfilePicture(storedProfilePicture);
        }
    }, []);

    const getInitials = (name) => {
        if (!name) return '';
        const names = name.split(' ');
        return names.map(n => n[0].toUpperCase()).join('');
    };

    const handleProfilePictureChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfilePicture(reader.result);
                localStorage.setItem('profilePicture', reader.result); // Almacena en localStorage
                updateNavbarProfilePicture(reader.result); // Actualiza la imagen en el navbar
            };
            reader.readAsDataURL(file);
        }
    };

    const handleRemoveProfilePicture = () => {
        setProfilePicture('');
        localStorage.removeItem('profilePicture'); // Elimina la foto del almacenamiento local
        updateNavbarProfilePicture(''); // Actualiza la imagen en el navbar
    };

    return (
        <div className="cuenta-container">
            <h1 className="cuenta-title">Mi Cuenta</h1>
            <div className="profile-section">
                <div className="profile-picture-container">
                    {profilePicture ? (
                        <>
                            <img src={profilePicture} alt="Foto de perfil" className="profile-picture" />
                            <button onClick={handleRemoveProfilePicture}>Eliminar Foto</button>
                        </>
                    ) : (
                        <div className="initials">{getInitials(fullName)}</div>
                    )}
                    <input 
                        type="file" 
                        accept="image/*" 
                        onChange={handleProfilePictureChange} 
                    />
                </div>
                <div className="profile-info">
                    <p><strong>Nombre de usuario:</strong> {username}</p>
                    <p><strong>Nombre completo:</strong> {fullName}</p>
                    <p><strong>Correo electrónico:</strong> {email}</p>
                    <p><strong>Fecha de registro:</strong> {registrationDate}</p>
                </div>
            </div>
            <button className="logout-btn">Cerrar sesión</button>
        </div>
    );
}

export default Cuenta;
