import React, { useState, useEffect } from 'react';

function Cuenta({ username }) {
    const [profilePicture, setProfilePicture] = useState('');
    const [file, setFile] = useState(null);

    useEffect(() => {
        const storedProfilePicture = localStorage.getItem('profilePicture');
        if (storedProfilePicture) {
            setProfilePicture(storedProfilePicture);
        }
    }, []);

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        if (selectedFile) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfilePicture(reader.result);
                localStorage.setItem('profilePicture', reader.result);
            };
            reader.readAsDataURL(selectedFile);
        }
    };

    const handleDeletePicture = () => {
        setProfilePicture('');
        localStorage.removeItem('profilePicture');
    };

    return (
        <div className="account">
            <h2>Mi Cuenta</h2>
            <div className="profile-picture-container">
                {profilePicture ? (
                    <img src={profilePicture} alt="Foto de perfil" className="profile-picture" />
                ) : (
                    <div className="initials">{getInitials(username)}</div>
                )}
                <div>
                    <input type="file" accept="image/*" onChange={handleFileChange} />
                    {profilePicture && <button onClick={handleDeletePicture}>Eliminar foto</button>}
                </div>
            </div>
            {/* Otras secciones de cuenta */}
        </div>
    );
}

function getInitials(name) {
    if (!name) return '';
    const names = name.split(' ');
    return names.map(n => n[0].toUpperCase()).join('');
}

export default Cuenta;
