import React, { useState } from 'react';
import './cuenta.css'; // Asegúrate de que esté importado

function Account() {
    const [profilePicture, setProfilePicture] = useState('');
    const [username, setUsername] = useState(localStorage.getItem('username') || '');
    
    const handleProfilePictureChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setProfilePicture(reader.result);
                localStorage.setItem('profilePicture', reader.result); // Almacenar en localStorage
            };
            reader.readAsDataURL(file);
        }
    };

    return (
        <div className="cuenta-container">
            <h2 className="cuenta-title">Cuenta</h2>
            <div className="profile-section">
                <div className="profile-picture-container">
                    {profilePicture ? (
                        <img src={profilePicture} alt="Foto de perfil" className="profile-picture" />
                    ) : (
                        <div className="initials">{getInitials(username)}</div>
                    )}
                    <input 
                        type="file" 
                        accept="image/*" 
                        onChange={handleProfilePictureChange}
                    />
                </div>
                <div className="profile-info">
                    <p>Nombre de usuario: {username || 'Usuario'}</p>
                </div>
            </div>
        </div>
    );
}

const getInitials = (name) => {
    if (!name) return '';
    const names = name.split(' ');
    return names.map(n => n[0].toUpperCase()).join('');
};

export default Account;
